/**
 * Print html markup if data exist and not empty print_markup
 * 1. var/key or arrya
 * 2. start markup
 * 3. end markup
 * 4. array 
 */
function coder618_print_markup($val_or_key = '', $markup_start = '', $markup_end = '', $arr = false){
    // if array provide
    if (is_array($arr)) {
        if (
            array_key_exists($val_or_key, $arr) &&
            !empty($arr[$val_or_key]) && strlen($arr[$val_or_key]) > 0
        ) {
            echo $markup_start . $arr[$val_or_key] . $markup_end;
        }
    } else {
        if (!empty($val_or_key) && strlen(trim($val_or_key)) > 0) {
            echo $markup_start . $val_or_key . $markup_end;
        }
    }
}

/**
 * Carbon field functions
 */
function getData(){
    return get_query_var('component_data', []);
}

function setData($data){
    return set_query_var('component_data', $data);
}



